import Room from "../components/Room";
import GlobalStyle from "../components/styles/GlobalStyles";
import { connect } from "react-redux";
import StyledRoomContainer from "../components/styles/StyledRoomContainer";
import { checkRoom, submitRooms } from "../redux/actions/roomActions";
import PropTypes from "prop-types";

const Index = ({ roomObj: { rooms, checkedRooms }, submitRooms }) => {
  return (
    <>
      <h1>Rooms</h1>
      <GlobalStyle />
      <StyledRoomContainer>
        {rooms.map(room => {
          return <Room key={room.id} room={room} />;
        })}
      </StyledRoomContainer>
      <button
        onClick={() => {
          submitRooms();
        }}
      >
        Submit
      </button>
      {checkedRooms && (
        <>
          <h5>Rooms you submitted:</h5>
          <div>
            {checkedRooms.map(room => {
              return (
                <div key={room.id}>
                  <p>id: {room.id}</p>
                  <p>adults: {room.adults}</p>
                  <p>children: {room.children}</p>
                </div>
              );
            })}
          </div>
        </>
      )}
    </>
  );
};

Index.getStaticProps = ({ store }) => {
  // console.log("s", store);
};
const mapStateToProps = state => ({
  roomObj: state.roomObj
});
Index.propTypes = {
  roomObj: PropTypes.object.isRequired,
  checkRoom: PropTypes.func.isRequired,
  submitRooms: PropTypes.func.isRequired
};
export default connect(mapStateToProps, { checkRoom, submitRooms })(Index);
