export const nextPage = () => (dispatch, getState) => {
  //console.log("next page...");
  const currentpage = getState().movies_obj.page;
  if (getState().movies_obj.page < 500) {
    dispatch({
      type: CHANGE_PAGE,
      payload: currentpage + 1
    });
  }
};
