import { CHECK_ROOM, SUBMIT_ROOM } from "../actions/types";

const initialState = {
  rooms: [
    { id: 1, ableToCheck: false, adults: 1, children: 0 },
    { id: 2, ableToCheck: true, checked: false, adults: 1, children: 0 },
    { id: 3, ableToCheck: true, checked: false, adults: 1, children: 0 },
    { id: 4, ableToCheck: true, checked: false, adults: 1, children: 0 }
  ],
  checkedRooms: []
};

export default (state = initialState, action) => {
  switch (action.type) {
    case CHECK_ROOM:
      console("payload", action.payload);
      return {
        ...state,
        rooms: action.payload
      };
    case SUBMIT_ROOM:
      return {
        ...state,
        checkedRooms: action.payload
      };
    default:
      return state;
  }
};
