import styled from "styled-components";

const StyledRoomTitle = styled.div`
  padding-left: 0.1em;
  display: flex;
  flex-direction: row;
  justify-content: start;
  align-items: center;
  background-color: ${room => {
    console.log(room);
    if (!room.ableToCheck || room.checked) {
      return "#ededed";
    }
    if (room.ableToCheck && !room.checked) {
      return "gray";
    }
  }};
  margin-bottom: 0.2em;
`;
export default StyledRoomTitle;
