import { CHECK_ROOM, SUBMIT_ROOM } from "./types";

export const checkRoom = id => (dispatch, getState) => {
  //const rooms = getState().roomObj.rooms;
  //console.log(rooms);

  // const rooms = getState().roomObj.rooms.map(room => {
  //   return room.id === id && room.ableToCheck === true
  //     ? {
  //         ...room,
  //         checked: !room.checked
  //       }
  //     : room;
  // });
  const rooms = [
    { id: 1, ableToCheck: false, adults: 1, children: 0 },
    { id: 2, ableToCheck: true, checked: false, adults: 1, children: 0 },
    { id: 3, ableToCheck: true, checked: false, adults: 1, children: 0 },
    { id: 4, ableToCheck: true, checked: false, adults: 1, children: 0 }
  ];

  dispatch({
    type: CHECK_ROOM,
    payload: rooms
  });
};
export const roomAutoChecks = id => (dispatch, getState) => {
  let checkedRoom = getState().roomObj.rooms.find(room => {
    if (room.id === id) {
      return room;
    }
  });
  let rooms = getState().roomObj.rooms;
  if (checkedRoom.checked === true) {
    rooms = rooms.map(room => {
      return room.id < id && room.ableToCheck === true
        ? {
            ...room,
            checked: true
          }
        : room;
    });
  } else {
    rooms = rooms.map(room => {
      return room.id > id && room.ableToCheck === true
        ? {
            ...room,
            checked: false
          }
        : room;
    });
  }
  dispatch({
    type: CHECK_ROOM,
    payload: rooms
  });
};

export const setAdultsOrChildren = (e, id) => (dispatch, getState) => {
  let rooms = getState().roomObj.rooms;
  rooms = rooms.map(room => {
    return room.id === id
      ? {
          ...room,
          [e.target.id]: e.target.value
        }
      : room;
  });
  dispatch({
    type: CHECK_ROOM,
    payload: rooms
  });
};

export const submitRooms = () => (dispatch, getState) => {
  let rooms = getState().roomObj.rooms;
  const checkedRooms = rooms.filter(room => {
    return (
      (room.checked || !room.ableToCheck) && {
        ...room
      }
    );
  });
  console.log(checkedRooms);
  dispatch({
    type: SUBMIT_ROOM,
    payload: checkedRooms
  });
};
