import StyledRoom from "./styles/StyledRoom";
import StyledRoomDisabled from "./styles/StyledRoomDisabled";
import StyledRoomName from "./styles/StyledRoomName";
import StyledRoomTitle from "./styles/StyledRoomTitle";
import StyledRoomTitleDisabled from "./styles/StyledRoomTitleDisabled";
import StyledSelects from "./styles/StyledSelects";

const Room = ({ room }) => {
  if (!room.ableToCheck || room.checked) {
    const Title = StyledRoomTitle;
  } else {
    const Title = StyledRoomTitleDisabled;
  }

  return (
    <StyledRoom>
      <Title>
        {room.ableToCheck && (
          <input
            type="checkbox"
            defaultChecked={room.checked ? "checked" : ""}
          />
        )}
        <span>
          <StyledRoomName>Room{room.id}</StyledRoomName>
        </span>
      </Title>
      <StyledSelects>
        <div>
          <label htmlFor="adults">Adults</label>
          <p>(18+)</p>
          <select id="adults">
            <option>1</option>
            <option>2</option>
          </select>
        </div>
        <div>
          <label htmlFor="children">Children</label>
          <p>(0-17)</p>
          <select id="children">
            <option>0</option>
            <option>1</option>
            <option>2</option>
          </select>
        </div>
      </StyledSelects>
    </StyledRoom>
  );
};

export default Room;
