import StyledRoom from "./styles/StyledRoom";
import StyledRoomDisabled from "./styles/StyledRoomDisabled";
import StyledRoomName from "./styles/StyledRoomName";
import StyledRoomTitle from "./styles/StyledRoomTitle";
import StyledRoomTitleDisabled from "./styles/StyledRoomTitleDisabled";
import StyledSelects from "./styles/StyledSelects";
import { connect } from "react-redux";
import { checkRoom, roomAutoChecks } from "../redux/actions/roomActions";
const Room = ({ room, checkRoom, roomAutoChecks }) => {
  let Title, RoomDiv, option;
  if (!room.ableToCheck || room.checked) {
    option = false;
  } else {
    option = true;
  }

  return (
    <StyledRoom room={room}>
      <StyledRoomTitle room={room} className="title">
        {room.ableToCheck && (
          <input
            type="checkbox"
            checked={room.checked}
            onChange={() => {
              checkRoom(room.id);
              roomAutoChecks(room.id);
            }}
          />
        )}
        <span>
          <StyledRoomName>Room{room.id}</StyledRoomName>
        </span>
      </StyledRoomTitle>
      <StyledSelects>
        <div>
          <label htmlFor="adults">Adults</label>
          <p>(18+)</p>
          <select id="adults" disabled={option}>
            <option>1</option>
            <option>2</option>
          </select>
        </div>
        <div>
          <label htmlFor="children">Children</label>
          <p>(0-17)</p>
          <select id="children" disabled={option} value={room.children}>
            <option>0</option>
            <option>1</option>
            <option>2</option>
          </select>
        </div>
      </StyledSelects>
    </StyledRoom>
  );
};

// export default Room;

export default connect(null, { checkRoom, roomAutoChecks })(Room);
