import StyledRoom from "./styles/StyledRoom";
import StyledRoomDisabled from "./styles/StyledRoomDisabled";
import StyledRoomName from "./styles/StyledRoomName";
import StyledRoomTitle from "./styles/StyledRoomTitle";
import StyledRoomTitleDisabled from "./styles/StyledRoomTitleDisabled";
import StyledSelects from "./styles/StyledSelects";

const Room = ({ room, checkRoom }) => {
  let Title, RoomDiv, option;
  if (!room.ableToCheck || room.checked) {
    Title = StyledRoomTitle;
    RoomDiv = StyledRoom;
    option = false;
  } else {
    Title = StyledRoomTitleDisabled;
    RoomDiv = StyledRoomDisabled;
    option = true;
  }

  return (
    <RoomDiv>
      <Title>
        {room.ableToCheck && (
          <input
            type="checkbox"
            defaultChecked={room.checked}
            onChange={checkRoom(room.id)}
          />
        )}
        <span>
          <StyledRoomName>Room{room.id}</StyledRoomName>
        </span>
      </Title>
      <StyledSelects>
        <div>
          <label htmlFor="adults">Adults</label>
          <p>(18+)</p>
          <select id="adults" disabled={option}>
            <option>1</option>
            <option>2</option>
          </select>
        </div>
        <div>
          <label htmlFor="children">Children</label>
          <p>(0-17)</p>
          <select id="children" disabled={option}>
            <option>0</option>
            <option>1</option>
            <option>2</option>
          </select>
        </div>
      </StyledSelects>
    </RoomDiv>
  );
};

export default Room;
