import { CHECK_ROOM } from "./types";

export const checkRoom = id => (dispatch, getState) => {
  console.log(id);
  let rooms = getState().roomObj.rooms;

  if (id === 2) {
    dispatchRoom(id, rooms);
  }
};
const dispatchRoom = (id, rooms) => (dispatch, getState) => {
  rooms = rooms.map(room => {
    return room.id === id && room.ableToCheck === true
      ? {
          ...room,
          checked: !room.checked
        }
      : room;
  });
  dispatch({
    type: CHECK_ROOM,
    payload: rooms
  });
};
